import React, { useContext, useState } from "react";
import { CartContext } from "../context/CartContext";
import { useNavigate, useLocation } from "react-router-dom";

function Cart() {
  const navigate = useNavigate();
  const location = useLocation();
  const { cart, increase, decrease, removeFromCart, totalPrice, clearCart } = useContext(CartContext);

  const [step, setStep] = useState(1); // 1: carrito, 2: envío, 3: pago, 4: confirmación
  const [formData, setFormData] = useState({
    nombre: "",
    email: "",
    direccion: "",
    tarjeta: "",
    expiracion: "",
    cvc: "",
  });
  const [errors, setErrors] = useState({});

  const token = localStorage.getItem("token");

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const validateShipping = () => {
    const newErrors = {};
    if (!formData.nombre) newErrors.nombre = "Nombre es requerido";
    if (!formData.email) newErrors.email = "Email es requerido";
    if (!formData.direccion) newErrors.direccion = "Dirección es requerida";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const validatePayment = () => {
    const newErrors = {};
    if (!formData.tarjeta.match(/^\d{16}$/)) newErrors.tarjeta = "Número de tarjeta inválido";
    if (!formData.expiracion.match(/^(0[1-9]|1[0-2])\/\d{2}$/)) newErrors.expiracion = "Expiración inválida MM/AA";
    if (!formData.cvc.match(/^\d{3,4}$/)) newErrors.cvc = "CVC inválido";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleProceedToShipping = () => {
    if (!token) {
      navigate("/login", { state: { from: location.pathname } });
      return;
    }
    setStep(2);
  };

  const handleProceedToPayment = () => {
    if (validateShipping()) setStep(3);
  };

  const handleConfirmOrder = () => {
    if (validatePayment()) {
      setStep(4);
      setTimeout(() => clearCart(), 2000);
    }
  };

  return (
    <div className="container mt-5">
      <h2 className="mb-4 text-center">🛒 Carrito de Compras</h2>

      {/* Step 1: Carrito */}
      {step === 1 && (
        <>
          {cart.length === 0 ? (
            <div className="alert alert-info text-center">
              Tu carrito está vacío. ¡Agrega productos!
              <p></p>
              <button className="btn btn-primary mt-2" onClick={() => navigate("/")}>Ir a la Tienda</button>
            </div>
          ) : (
            <>
              <table className="table table-bordered align-middle text-center shadow-sm">
                <thead className="table-dark">
                  <tr>
                    <th>Producto</th>
                    <th>Precio</th>
                    <th>Cantidad</th>
                    <th>Subtotal</th>
                  </tr>
                </thead>
                <tbody>
                  {cart.map((product) => (
                    <tr key={product.id}>
                      <td className="text-start">
                        <img src={product.image} alt={product.name} style={{ width: "60px", height: "60px", objectFit: "cover", marginRight: "10px", borderRadius: "5px" }} />
                        {product.name}
                      </td>
                      <td>${product.price.toFixed(2)}</td>
                      <td>
                        <div className="d-flex justify-content-center align-items-center">
                          <button className="btn btn-sm btn-secondary me-2" onClick={() => decrease(product.id)}>➖</button>
                          <span className="fw-bold">{product.quantity}</span>
                          <button className="btn btn-sm btn-secondary ms-2" onClick={() => increase(product.id)}>➕</button>
                          <button className="btn btn-sm btn-danger ms-2" onClick={() => removeFromCart(product.id)}>❌</button>
                        </div>
                      </td>
                      <td>${(product.price * product.quantity).toFixed(2)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>

              <div className="text-end mt-4">
                <h4>Total: <span className="text-success">${totalPrice.toFixed(2)}</span></h4>
                <button className="btn btn-success mt-2" onClick={handleProceedToShipping}>💳 Proceder al envío</button>
              </div>
            </>
          )}
        </>
      )}

      {/* Step 2: Datos de envío */}
      {step === 2 && (
        <div className="card shadow p-4">
          <h4 className="mb-3">📋 Datos de Envío</h4>
          <div className="mb-2">
            <input type="text" name="nombre" placeholder="Nombre completo" className="form-control" value={formData.nombre} onChange={handleInputChange} />
            {errors.nombre && <small className="text-danger">{errors.nombre}</small>}
          </div>
          <div className="mb-2">
            <input type="email" name="email" placeholder="Correo electrónico" className="form-control" value={formData.email} onChange={handleInputChange} />
            {errors.email && <small className="text-danger">{errors.email}</small>}
          </div>
          <div className="mb-2">
            <input type="text" name="direccion" placeholder="Dirección de entrega" className="form-control" value={formData.direccion} onChange={handleInputChange} />
            {errors.direccion && <small className="text-danger">{errors.direccion}</small>}
          </div>
          <div className="text-end">
            <button className="btn btn-secondary me-2" onClick={() => setStep(1)}>⬅ Volver</button>
            <button className="btn btn-primary" onClick={handleProceedToPayment}>Siguiente ➡</button>
          </div>
        </div>
      )}

      {/* Step 3: Pago */}
      {step === 3 && (
        <div className="card shadow p-4">
          <h4 className="mb-3">💳 Pago</h4>
          <div className="mb-2">
            <input type="text" name="tarjeta" placeholder="Número de tarjeta (16 dígitos)" className="form-control" value={formData.tarjeta} onChange={handleInputChange} maxLength={16} />
            {errors.tarjeta && <small className="text-danger">{errors.tarjeta}</small>}
          </div>
          <div className="row">
            <div className="col-md-6 mb-2">
              <input type="text" name="expiracion" placeholder="MM/AA" className="form-control" value={formData.expiracion} onChange={handleInputChange} maxLength={5} />
              {errors.expiracion && <small className="text-danger">{errors.expiracion}</small>}
            </div>
            <div className="col-md-6 mb-2">
              <input type="text" name="cvc" placeholder="CVC" className="form-control" value={formData.cvc} onChange={handleInputChange} maxLength={4} />
              {errors.cvc && <small className="text-danger">{errors.cvc}</small>}
            </div>
          </div>
          <div className="text-end">
            <button className="btn btn-secondary me-2" onClick={() => setStep(2)}>⬅ Volver</button>
            <button className="btn btn-success" onClick={handleConfirmOrder}>Confirmar Pedido ✅</button>
          </div>
        </div>
      )}

      {/* Step 4: Confirmación */}
      {step === 4 && (
        <div className="alert alert-success text-center p-4 shadow">
          <h3>🎉 ¡Gracias por tu compra!</h3>
          <p>Tu pedido ha sido procesado y pronto lo recibirás en tu dirección.</p>
        </div>
      )}
    </div>
  );
}

export default Cart;
