import React, { useContext, useState } from "react";
import { CartContext } from "../context/CartContext";
import { useNavigate, useLocation } from "react-router-dom";

function Cart() {
  const navigate = useNavigate();
  const location = useLocation();
  const { cart, increase, decrease, removeFromCart, totalPrice, clearCart } = useContext(CartContext);

  const [step, setStep] = useState(1); // 1: carrito, 2: datos, 3: pago, 4: confirmaci√≥n
  const [formData, setFormData] = useState({
    nombre: "",
    apellido: "",
    email: "",
    telefono: "",
    direccion: "",
    ciudad: "",
    codigoPostal: "",
    instrucciones: "",
    tarjeta: "",
    expiracion: "",
    cvc: "",
  });

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const token = localStorage.getItem("token");

  const handleProceedToPayment = () => {
    if (token) {
      setStep(2);
    } else {
      navigate("/login", { state: { from: location.pathname } });
    }
  };

  const handleConfirmOrder = () => {
    setStep(4);
    setTimeout(() => clearCart(), 2000);
  };

  return (
    <div className="container mt-5">
      <h2 className="mb-4 text-center">üõí Tu Carrito de Compras</h2>

      {/* Step 1: Carrito */}
      {step === 1 && (
        <>
          {cart.length === 0 ? (
            <div className="alert alert-info text-center">
              Tu carrito est√° vac√≠o. ¬°Agrega algunos productos!
              <p></p>
              <button className="btn btn-primary mt-2" onClick={() => navigate("/")}>
                Ir a la Tienda
              </button>
            </div>
          ) : (
            <>
              <table className="table table-bordered align-middle text-center shadow-sm">
                <thead className="table-dark">
                  <tr>
                    <th>Producto</th>
                    <th>Precio</th>
                    <th>Cantidad</th>
                    <th>Subtotal</th>
                  </tr>
                </thead>
                <tbody>
                  {cart.map((product) => (
                    <tr key={product.id}>
                      <td className="text-start">
                        <img
                          src={product.image}
                          alt={product.name}
                          style={{ width: "60px", height: "60px", objectFit: "cover", marginRight: "10px", borderRadius: "5px" }}
                        />
                        {product.name}
                      </td>
                      <td>${product.price.toFixed(2)}</td>
                      <td>
                        <div className="d-flex justify-content-center align-items-center">
                          <button className="btn btn-sm btn-secondary me-2" onClick={() => decrease(product.id)}>‚ûñ</button>
                          <span className="fw-bold">{product.quantity}</span>
                          <button className="btn btn-sm btn-secondary ms-2" onClick={() => increase(product.id)}>‚ûï</button>
                          <button className="btn btn-sm btn-danger ms-2" onClick={() => removeFromCart(product.id)}>‚ùå</button>
                        </div>
                      </td>
                      <td>${(product.price * product.quantity).toFixed(2)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>

              <div className="text-end mt-4">
                <h4>Total: <span className="text-success">${totalPrice.toFixed(2)}</span></h4>
                <button className="btn btn-success mt-2" onClick={handleProceedToPayment}>
                  üí≥ Proceder al Pago
                </button>
              </div>
            </>
          )}
        </>
      )}

      {/* Step 2: Datos de env√≠o */}
      {step === 2 && (
        <div className="card shadow p-4">
          <h4 className="mb-3">üìã Datos de Env√≠o</h4>
          <div className="row">
            <div className="col-md-6 mb-3">
              <input type="text" name="nombre" placeholder="Nombre" className="form-control" value={formData.nombre} onChange={handleInputChange} />
            </div>
            <div className="col-md-6 mb-3">
              <input type="text" name="apellido" placeholder="Apellido" className="form-control" value={formData.apellido} onChange={handleInputChange} />
            </div>
          </div>
          <div className="mb-3">
            <input type="email" name="email" placeholder="Correo electr√≥nico" className="form-control" value={formData.email} onChange={handleInputChange} />
          </div>
          <div className="mb-3">
            <input type="tel" name="telefono" placeholder="Tel√©fono" className="form-control" value={formData.telefono} onChange={handleInputChange} />
          </div>
          <div className="mb-3">
            <input type="text" name="direccion" placeholder="Direcci√≥n" className="form-control" value={formData.direccion} onChange={handleInputChange} />
          </div>
          <div className="row">
            <div className="col-md-6 mb-3">
              <input type="text" name="ciudad" placeholder="Ciudad" className="form-control" value={formData.ciudad} onChange={handleInputChange} />
            </div>
            <div className="col-md-6 mb-3">
              <input type="text" name="codigoPostal" placeholder="C√≥digo Postal" className="form-control" value={formData.codigoPostal} onChange={handleInputChange} />
            </div>
          </div>
          <div className="mb-3">
            <textarea name="instrucciones" placeholder="Instrucciones adicionales" className="form-control" value={formData.instrucciones} onChange={handleInputChange} rows={2} />
          </div>

          <div className="text-end">
            <button className="btn btn-secondary me-2" onClick={() => setStep(1)}>‚¨Ö Volver</button>
            <button className="btn btn-primary" onClick={() => setStep(3)}>Siguiente ‚û°</button>
          </div>
        </div>
      )}

      {/* Step 3: Pago */}
      {step === 3 && (
        <div className="card shadow p-4">
          <h4 className="mb-3">üí≥ M√©todo de Pago</h4>
          <div className="mb-3">
            <input type="text" name="tarjeta" placeholder="N√∫mero de tarjeta" className="form-control" value={formData.tarjeta} onChange={handleInputChange} maxLength={19} />
          </div>
          <div className="row">
            <div className="col-md-6 mb-3">
              <input type="text" name="expiracion" placeholder="MM/AA" className="form-control" value={formData.expiracion} onChange={handleInputChange} maxLength={5} />
            </div>
            <div className="col-md-6 mb-3">
              <input type="text" name="cvc" placeholder="CVC" className="form-control" value={formData.cvc} onChange={handleInputChange} maxLength={4} />
            </div>
          </div>
          <div className="text-end">
            <button className="btn btn-secondary me-2" onClick={() => setStep(2)}>‚¨Ö Volver</button>
            <button className="btn btn-success" onClick={handleConfirmOrder}>Confirmar Pedido ‚úÖ</button>
          </div>
        </div>
      )}

      {/* Step 4: Confirmaci√≥n */}
      {step === 4 && (
        <div className="alert alert-success text-center p-4 shadow">
          <h3>üéâ ¬°Gracias por tu compra!</h3>
          <p>Tu pedido ha sido procesado y pronto lo recibir√°s en tu direcci√≥n.</p>
        </div>
      )}
    </div>
  );
}

export default Cart;
